import React, { Component } from 'react'
import { connect } from 'react-redux'
import { sendRecipeSelection } from '../../actions/myRecipes'

class Recipe extends Component {

  handleOnClick = (event, props) => {
    debugger
    const recipeSelection = this.props.recipes.filter(recipe => recipe.id === event.target.dataset.id)[0]
    this.props.sendRecipeSelection(recipeSelection)
  }

  render() {
    return(
      <div key={this.props.recipe.id} data-id={this.props.recipe.id} className="card" onClick={this.handleOnClick(event, this.props)}>
        <h3 data-id={this.props.recipe.id}>{this.props.recipe.name}</h3>
      </div>
    )
  }

}

const mapStateToProps = (state) => {
  return {
    recipes: state.recipes,
  }
}


export default connect(mapStateToProps, { sendRecipeSelection })(Recipe)
