import React, { Component } from 'react';
import IngredientForm from './ingredientForm';
import { connect } from 'react-redux';
import { createNewDBRecipe } from '../../actions/myRecipes'



class RecipeForm extends Component {

  constructor() {
    super()
    this.state = {
      name: '',
      directions: '',
      ingredients_attributes: []
    };
    this.ingredientCounter = 1;
    this.ingredientFormArray = [1];
  }

  createIngredientObjects = () => {
    let arrayOfIngredients = [];
    let allInputs = document.getElementsByClassName("ingredient");
    let uniqueInputs = [];

    for (let i = 0; i < allInputs.length; i++) {
      if (!uniqueInputs.includes(allInputs[i].classList[1])) {
        uniqueInputs.push(allInputs[i].classList[1])
      }
    }

    for (let i = 0; i < uniqueInputs.length; i++) {
      let newIng = {
        quantity: document.getElementsByClassName(uniqueInputs[i]).quantity.value,
        unit: document.getElementsByClassName(uniqueInputs[i]).unit.value,
        description: document.getElementsByClassName(uniqueInputs[i]).description.value
      };
      arrayOfIngredients.push(newIng)
      console.log(ingredientFormArray)
      debugger
    }

    console.log(arrayOfIngredients)
  }

  handleOnSubmit = event => {
    event.preventDefault();
    this.createIngredientObjects()
    // let ingredientsArray = [];
    // let recipeObject = {
    //   name: '',
      //   directions: ''.
      //   ingredients_attributes: [
      //      quantity: '',
      //      unit: '',
      //      description: '',
    // }
    // this.props.createNewDBRecipe(this.props.recipeFormData)
  // recipeFormData needs to be in this format: {
  //   name: '',
  //   directions: ''.
  //   ingredients_attributes: [
  //      quantity: '',
  //      unit: '',
  //      description: '',
  //    ]
  // }
  // document.getElementsByClassName("ingredient-1").quantity.value
}

  handleOnClick = event => {
    event.preventDefault();
    ++ingredientCounter;
    ingredientFormArray.push(ingredientCounter);
    this.forceUpdate()
  }

  render() {
    // debugger
    return (
      <div className="card card-holder">
        <form id="recipe-form" onSubmit={this.handleOnSubmit}>
          <p>
            <label htmlFor="name"><strong>Recipe Name: </strong></label>
            <input type="text" name="name" value={this.state.name} onChange={this.handleOnChange}/>
          </p>
          <p>
            <label htmlFor="directions"><strong>Directions:</strong> </label>
            <textarea name="directions"/>
          </p>
          <br/>
          {ingredientFormArray.map(i => <IngredientForm count={i} createRecipeObj={this.createRecipeObj}/>)}
          <br />
          <button onClick={this.handleOnClick}>Add another ingredient:</button>
          <br />
          <input type="submit" />
        </form>
      </div>
    )
  }

}

export default connect(null, { createNewDBRecipe })(RecipeForm)
