const API_URL = process.env.REACT_APP_API_URL;

const setRecipes = recipes => {
  return {
    type: "LOADED_USER_RECIPES",
    recipes
  }
}

export const getMyRecipes = () => {
  // debugger
  return dispatch => {
    return fetch(`${API_URL}/recipes`)
    .then(response => response.json())
    .then(recipes => dispatch(
      setRecipes(recipes))
    )
  }
}
