import React, { Component } from 'react';
import IngredientForm from './ingredientForm';
import { connect } from 'react-redux';
import { createNewDBRecipe } from '../../actions/myRecipes'

let ingredientCounter = 1;
let ingredientFormArray = [1];

class RecipeForm extends Component {

  constructor() {
    super()
    this.state = {
      name: '',
      directions: '',
      ingredients_attributes: []
    }
  }

  createRecipeObj = () => {
    // debugger
    let newIngredient =
      `{quantity: ${document.querySelector("input[name='quantity']").value},
      unit: ${document.querySelector("input[name='unit']").value},
      description: ${document.querySelector("input[name='description']").value}}`;
    this.setState({
      ingredients_attributes: [...this.state.ingredients_attributes, newIngredient]
    })
  }

  // componentDidUpdate = (event) => {
  //   let newRecipeObj = {
  //     quantity: event.target.quantity,
  //     unit: event.target.unit,
  //     description: event.target.description
  //   };
  //   this.setState({
  //     ingredients_attributes: [...this.state.ingredients_attributes, newRecipeObj] })
  // }

  handleOnSubmit = event => {
    event.preventDefault();
    this.props.createNewDBRecipe(this.props.recipeFormData)
  // recipeFormData needs to be in this format: {
  //   name: '',
  //   directions: ''.
  //   ingredients_attributes: [
  //      quantity: '',
  //      unit: '',
  //      description: '',
  //    ]
  // }
}

  handleOnClick = event => {
    event.preventDefault();
    console.log("button clicked");
    ++ingredientCounter;
    ingredientFormArray.push(ingredientCounter);
    this.forceUpdate()
  }

  render() {
    // debugger
    return (
      <div className="card card-holder">
        <form onSubmit={this.handleOnSubmit}>
          <p>
            <label htmlFor="name"><strong>Recipe Name: </strong></label>
            <input type="text" name="name" value={this.props.recipeFormData.name} onChange={this.handleOnChange}/>
          </p>
          <p>
            <label htmlFor="directions"><strong>Directions:</strong> </label>
            <textarea name="directions"/>
          </p>
          <br/>
          {ingredientFormArray.map(i => <IngredientForm count={i} createRecipeObj={this.createRecipeObj}/>)}
          <br />
          <button onClick={this.handleOnClick}>Add another ingredient:</button>
          <br />
          <input type="submit" />
        </form>
      </div>
    )
  }

}

const mapStateToProps = state => {
  return {
    recipeFormData: state.recipes.recipeFormData
  }
}

export default connect(mapStateToProps, { createNewDBRecipe })(RecipeForm)


